package loopingControls;

public class FarmerSeeds {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=3;i++)//for rows
		{
			for(int j=1;j<=5;j++)//for spots
			{
				System.out.println("Farmser has soared the seed in"+i+"in spot"+j);
				
			}
			
		}
		System.out.println("Complted planting in all the rows");
	

	}

}
