package loopingControls;

public class PrintNumbersUsingDowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*In do while loop first doing one activity after that
		 increment or decrement the value after that in while will 
		 check the condition if it is true will do instruction when 
		 condition false the control will come out of loop  
		 */
        int i=1;//step1
        do//Here instructions  will be written 
        {
        	System.out.println(i);//step2
        	i++;//step3
        }
        while(i<=5);//semicolon comes here.step4
	}

}
