package loopingControls;

public class PrintNumbersUsingForloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++)/*Number starts from 1 and end at 5 
		i Value increment if condition is true up to condition false*/
		{
			System.out.println(i);//Printing the i value in 'i' the numbers will be stored
		}

	}

}
