package loopingControls;

public class PrintNumbersUsingWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*In this while loop First initializing the value then 
		 second step is condition check third step  is instructions
		 after that increment or decrement the value */
		 int i=1;//step1
		 while(i<=5)/*if condition  is true it print i when condition 
		 false it will come out of the loop.Step2*/
		 {	 
			System.out.println(i);//step3
			i++;//step4
		 }
		

	}

}
